from flask import Flask

app = Flask(__name__)

@app.route('/')
def index():
    return 'Hello World!'

if __name__ == '__main__':
    app.run(debug=True)

@app.route('/hello')
def hello():
    return 'Hello Year 10!'

@app.route('/school')
def school():
    return "St Aidan's Anglican Girls School"
